mapper.select:
select i.i_no as "i_no"
,(select a.ia_name from code_ingredient_alpha a where a.ia_code=i.ia_code) as "ia_name" ,(select b.ib_name from code_ingredient_beta b where b.ib_code=i.ib_code) as "ib_name" ,(select o.io_name from code_ingredient_origin o where o.io_code=i.io_code) as"io_name" ,i.i_name as "i_name" ,i.i_size as "i_size" ,to_char(i.i_price, '9,999,999,999') as "i_price" ,to_char(i.reg_date,'yyyy-mm-dd(dy) hh:mi') as "reg_date" ,i.is_del as "is_del"
from ingredient i where i.is_del = 'F' and s_no = (select s_no from store where s_id=#{s_id})

mapper.update:
update ingredient set
ia_code=(select ia_code from code_ingredient_alpha where ia_name=#{ia_name})
,ib_code=(select ib_code from code_ingredient_beta where ib_name=#{ib_name})
,io_code=(select io_code from code_ingredient_origin where io_name=#{io_name})
,i_name  = #{i_name}
,i_size  = #{i_size}
,i_price = to_number(${i_price}, 9999999999)
where i_no = ${i_no}

update ingredient set
is_del = 'T'
where i_no = ${i_no}

mapper.insert:
insert into ingredient_record(
	i_no
	,i_price
) values (
	${i_no}
	to_number(${change_i_price}, 9999999999)
)

