mapper.insert:
insert into stock(
	st_no
	,i_no
	,quantity
	,st_state
) values (
	(select nvl(max(st_no),0)+1 from stock)
	,${i_no}
	,${quantity}
	,#{st_state}
)

mapper.update:
update
	stock
set
	quantity = ${quantity}
	,st_state = #{st_state}
where
	st_no = ${st_no}

update
	stock
set
	is_del='F'
	,st_state = #{st_state}
	,quantity = ${quantity}
where
	is_del='T'
	and i_no = ${i_no}

mapper.insert:
insert into stock_record(
	st_no
	,quantity
) values (
	${st_no}
	,(select sum(quantity)*(-1) from stock_record where st_no=${st_no})
)

insert into stock_record(
	st_no
	,quantity
) values (
	(select st_no from stock where i_no=${i_no})
	,${quantity}
)
