mapper.insert:
insert into order_info(
	oi_no
	,s_no
	,c_phone
	,gender
	,age
	,pickup_time
)values(
	(select nvl(max(oi_no),0)+1 from order_info)
	,(select s_no from store where s_id=#{s_id})
	,#{c_phone}
	,#{gender}
	,#{age}
	, sysdate + ${pickup_time}
	
)

insert into order_menu(
	oi_no
	,mi_no
	,quantity
)values(
	(select nvl(max(oi_no),0) from order_info)
	,(select mi_no from menu_info where mi_name=#{mi_name} and is_del='F' and s_no = (select s_no from store where s_id=#{s_id}))
	,${quantity}
)

mapper.select:
select count(st_no) from stock where
quantity <=${quantity}
and i_no in (select i_no from menu_ingredient where mi_no = (select mi_no from menu_info where mi_name='설렁탕' and is_del='F' and s_no = (select s_no from store where s_id='abc123')) )

mapper.update:
update order_info 
set 
	c_phone=#{c_phone}
	,gender=#{gender}
	,age=#{age}
	,pickup_time=to_date(#{pickup_time},'YYYY-MM-DD(DY) HH:MI:SS')
where oi_no=${oi_no}

mapper.insert:
insert into order_menu(
	oi_no
	,mi_no
	,quantity
)values(
	${oi_no}
	,(select mi_no from menu_info  where mi_name=#{mi_name} and is_del='F' and is_del='F' and s_no = (select s_no from store where s_id=#{s_id}))
	,${quantity}
)

mapper.select:
		select zzz.*, rownum RNUM from
		(select
			count(mi_name)||''	"data", mi_name	 "label", to_char(order_time,'yyyy')||'년 '||to_char(order_time,'mm')||'월 '||to_char(order_time, 'w')||'주'	"dataset"
		from order_day_menu
			where
				s_no = (select s_no from store where s_id=#{s_id})
				and to_char(order_time,'yyyy')=#{year}
				and to_char(order_time,'mm')=#{month}
				and to_char(order_time, 'w')=#{week}
			group by  to_char(order_time,'yyyy')||'년 '||to_char(order_time,'mm')||'월 '||to_char(order_time, 'w')||'주', mi_name
			order by  count(mi_name) desc) zzz
		where
 			
 			 rownum<=${chart_cnt}
